/*
This is the Scratch 3 extension to remotely control an
MQTT
 */
// Boiler plate from the Scratch Team

const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const formatMessage = require('format-message');
const msg = require('./translation');

// mqtt Modes
let mqtt = require('mqtt');
const { locale } = require('core-js');

require('sweetalert');
//async await estea
const ml5 = require('ml5');
//require('babel-polyfill');


let the_locale = null;

//mqtt message
let mqtt_message = msg.disconnect[the_locale];
let client = {};
let clientId;

class Scratch3Mqtt {
    constructor(runtime) {
        the_locale = this._setLocale();
        this.runtime = runtime;
    }

    getInfo() {
        the_locale = this._setLocale();
        return {
            id: 'mqtt',
            color1: '#003D79', //'#0C5986',
            color2: '#34B0F7',
            name: 'MQTT',
            blockIconURI: 'data:image/png;base64,',
            blocks: [
                {
                    opcode: 'connect_mqtt',
                    blockType: BlockType.COMMAND,
                    text: msg.ContentPort[the_locale],
                    arguments: {
                        URL: {
                            type: ArgumentType.STRING,
                            defaultValue: 'ws://broker.hivemq.com:8000/mqtt',
                            //defaultValue: 'ws://broker.emqx.io:8083/mqtt'
                        },
                        USR: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        },
                        PWS: {
                            type: ArgumentType.STRING,
                            defaultValue: ' ',
                        }
                    },

                },
                {
                    opcode: 'connect_off',
                    blockType: BlockType.COMMAND,
                    text: msg.Content_off[the_locale],
                },
                {
                    opcode: 'subscribe_topic',
                    blockType: BlockType.COMMAND,
                    text: msg.Subscribe_Topic[the_locale],
                    arguments: {
                        TOPIC: {
                            type: ArgumentType.STRING,
                            defaultValue: 'osep',
                        }
                    },

                },
                {
                    opcode: 'publish_topic',
                    blockType: BlockType.COMMAND,
                    text: msg.Publish_Topic[the_locale],
                    arguments: {
                        TOPIC: {
                            type: ArgumentType.STRING,
                            defaultValue: 'osep',
                        },
                        PUBLISH: {
                            type: ArgumentType.STRING,
                            defaultValue: 'Hello osep',
                        }
                    },
                },
                {
                    opcode: 'mqtt_msg',
                    blockType: BlockType.REPORTER,
                    text: msg.FormDmqttMsg[the_locale],
                    arguments: {
                        /*PIN: {
                            type: ArgumentType.NUMBER,
                            defaultValue: '8',
                            menu: 'digital_pins'
                        },*/
                        id: {
                            type: ArgumentType.STRING,
                            defaultValue: 'id'
                        }
                    }
                },
            ],

        };
    }

    // command blocks
    mqtt_msg(args) {
        return mqtt_message;
    }
    async connect_off(args) {
        mqtt_message = msg.disconnect[the_locale];
        client.end();
        client = {};
    }

    async connect_mqtt(args) {
        //https://www.emqx.com/zh/blog/connect-to-mqtt-broker-with-websocket
        clientId = 'mqttjs_' + Math.random().toString(16).substr(2, 8);
        clientId = 'mqttx_' + Math.random().toString(16).substr(2, 8);
        let url = args.URL;
        let usr = args.USR;
        let pws = args.PWS;
        let options = {};
        if (usr == ' ') {
            options = {
                keepalive: 30,
                clientId: clientId,
                protocolId: 'MQTT',
                protocolVersion: 4,
                clean: true,
                reconnectPeriod: 1000,
                connectTimeout: 30 * 1000,
                will: {
                    topic: 'WillMsg',
                    payload: 'Connection Closed abnormally..!',
                    qos: 0,
                    retain: false
                },
                ejectUnauthorized: false
            }
        } else {
            options = {
                keepalive: 30,
                usr,
                pws,
                username: usr,
                password: pws,
                clientId: clientId,
                protocolId: 'MQTT',
                protocolVersion: 4,
                clean: true,
                reconnectPeriod: 1000,
                connectTimeout: 30 * 1000,
                will: {
                    topic: 'WillMsg',
                    payload: 'Connection Closed abnormally..!',
                    qos: 0,
                    retain: false
                },
                rejectUnauthorized: false
            }
        }

        client = mqtt.connect(url, options);
        client.on('error', function (err) {
            mqtt_message = err;
            alert(err);
            client.end()
        });

        client.on('connect', function () {
            console.log('client connected:' + clientId)
            mqtt_message = msg.isconnect[the_locale];
            //client.subscribe('osep', { qos: 0 })
            //client.publish('osep', 'wss secure connection demo...!', { qos: 0, retain: false })
        })
        /*
        client.on('message', function (topic, message, packet) {
          mqtt_message = message.toString() + '\nOn topic:= ' + topic;  
          console.log('Received Message:= ' + mqtt_message);
        })
        */

        client.on('close', function () {
            mqtt_message = msg.disconnect[the_locale];
            console.log(clientId + ' disconnected')
        })
    }

    async publish_topic(args) {
        const topic = args.TOPIC;
        const publish_string = args.PUBLISH;
        if (JSON.stringify(client) == '{}') {
            alert('not connect!');
        } else {
            client.publish(topic, publish_string, { qos: 0, retain: false });
            mqtt_message = topic + ':' + publish_string;
            console.log('topic,publish_string', topic + ',' + publish_string);
        }

    }

    async subscribe_topic(args) {
        /*if (!client.connected) {
            console.log('客戶端未連線')
            return
        }*/
        const topic = args.TOPIC;
        if (JSON.stringify(client) == '{}') {
            alert('not connect!');
        } else {
            mqtt_message = topic;
            client.subscribe(topic, { qos: 0 });
        }

    }

    // end of block handlers

    _setLocale() {
        let now_locale = '';
        switch (formatMessage.setup().locale) {
            case 'zh-tw':
                now_locale = 'zh-tw';
                break;
            default:
                now_locale = 'en';
                break;
        }
        return now_locale;
    }

    // helpers

}

module.exports = Scratch3Mqtt;
