const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const msg = require('./translation');
const formatMessage = require('format-message');
//const menuIconURI = null;
const menuIconURI = ' data:image/png;base64,';
//const blockIconURI = null;
const blockIconURI = 'data:image/png;base64,';

const defaultId = 'default';
let theLocale = null;
//語音辨識
v_textArray = [];
var recognition = new webkitSpeechRecognition();
recognition.continuous = true;
recognition.interimResults = true;
recognition.lang = "cmn-Hant-TW";
recognition.onstart = function () {
    console.log('開始辨識...');
};
recognition.onresult = function (event) {
    var i = event.resultIndex;
    var j = event.results[i].length - 1;
    v_textArray.pop();
    v_textArray.push(event.results[i][j].transcript);
    console.log('event:', v_textArray);
};

class voicetoTEXT {
    constructor(runtime) {
        theLocale = this._setLocale();
        this.runtime = runtime;
        // communication related
        this.comm = runtime.ioDevices.comm;
        this.session = null;
        this.runtime.registerPeripheralExtension('voicetoTEXT', this);
        // session callbacks
        this.reporter = null;
        this.onmessage = this.onmessage.bind(this);
        this.onclose = this.onclose.bind(this);
        this.write = this.write.bind(this);
        // string op
        this.decoder = new TextDecoder();
        this.lineBuffer = '';

        this.v_text = { data: "語音" }; //語音辨識

        this.emptyObj = {
            VALUE: {}
        };
    }

    onclose() {
        this.session = null;
    }

    write(data, parser = null) {
        if (this.session) {
            return new Promise(resolve => {
                if (parser) {
                    this.reporter = {
                        parser,
                        resolve
                    };
                }
                this.session.write(data);
            });
        }
    }

    onmessage(data) {
        const dataStr = this.decoder.decode(data);
        this.lineBuffer += dataStr;
        if (this.lineBuffer.indexOf('\n') !== -1) {
            const lines = this.lineBuffer.split('\n');
            this.lineBuffer = lines.pop();
            for (const l of lines) {
                if (this.reporter) {
                    const { parser, resolve } = this.reporter;
                    resolve(parser(l));
                }
            }
        }
    }

    scan() {
        this.comm.getDeviceList().then(result => {
            this.runtime.emit(this.runtime.constructor.PERIPHERAL_LIST_UPDATE, result);
        });
    }

    _setLocale() {
        let nowLocale = '';
        switch (formatMessage.setup().locale) {
            case 'zh-tw':
                nowLocale = 'zh-tw';
                break;
            default:
                nowLocale = 'en';
                break;
        }
        return nowLocale;
    }

    getInfo() {
        theLocale = this._setLocale();

        return {
            id: 'voicetoTEXT',
            name: msg.name[theLocale],
            color1: '#595a5c',
            color2: '#595a5c',
            menuIconURI: menuIconURI,
            blockIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'beginVoice',
                    blockType: BlockType.COMMAND,
                    text: msg.beginVoice[theLocale]
                },
                {
                    opcode: 'pauseVoice',
                    blockType: BlockType.COMMAND,
                    text: msg.pauseVoice[theLocale]
                },
                {
                    opcode: 'nullTEXT',
                    blockType: BlockType.COMMAND,
                    text: msg.nullTEXT[theLocale]
                },
                {
                    opcode: 'voicetoTEXT',
                    blockType: BlockType.REPORTER,
                    arguments: {
                        id: {
                            type: ArgumentType.STRING,
                            defaultValue: 'id'
                        }
                    },
                    text: msg.voicetoTEXT[theLocale]
                }
            ]
        };
    }

    beginVoice() {
        //	var recognition = new webkitSpeechRecognition();
        //	this.v_text.data= '';

        recognition.start();
    }

    pauseVoice() {
        recognition.abort();
    }

    nullTEXT() {
        recognition.abort();
        v_textArray[0] = "";
        this.v_text.data = v_textArray[0];
        recognition.start();
        return this.v_text.data;
    }

    voicetoTEXT() {
        this.v_text.data = v_textArray[0];
        return this.v_text.data;
    }

}

module.exports = voicetoTEXT;
